import React, { useState, useRef, useEffect } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";
import html2canvas from 'html2canvas';

function App() {
  const [screenshot, setScreenshot] = useState({
    username: "用户名",
    keepTitle: "户外跑步",
    miles: 2.5,
    speed: 5.5,
    date: new Date(),
    temperature: 12,
    humidity: 23,
  });

  const [bgImage, setBgImage] = useState("");
  const [portraitImage, setPortraitImage] = useState("");
  const [weatherImage, setWeatherImage] = useState("/images/weather1.png");

  const canvasRef = useRef(null);
  const newImgRef = useRef(null);

  useEffect(() => {
    // 初始化背景图片和其他默认值
    setBgImage("/images/bg1_1.png");
    setPortraitImage("/images/default_portrait.png");
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScreenshot((prev) => ({ ...prev, [name]: value }));
  };

  const handleBgImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setBgImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handlePortraitChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setPortraitImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleWeatherChange = (e) => {
    setWeatherImage(e.target.value);
  };

  const generateScreenshot = () => {
    html2canvas(newImgRef.current).then(canvas => {
      const link = document.createElement('a');
      link.download = `keep${screenshot.date.getMonth() + 1}月${screenshot.date.getDate()}日跑步打卡.png`;
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  return (
    <Container className="my-4">
      <h1 className="text-center mb-4">Keep运动截图生成器</h1>
      <Row>
        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>预览</Card.Title>
              <div
                ref={newImgRef}
                style={{
                  width: "360px",
                  height: "719px",
                  border: "1px solid #ddd",
                  position: "relative",
                  backgroundImage: `url(${bgImage})`,
                  backgroundSize: "cover",
                }}
              >
                <img src={weatherImage} alt="天气" style={{position: "absolute", top: "426.5px", left: "238px", width: "14px", height: "14px"}} />
                <img src={portraitImage} alt="头像" style={{position: "absolute", top: "335px", left: "284px", width: "40px", height: "40px", borderRadius: "20px"}} />
                <span style={{position: "absolute", top: "385px", right: "34px", fontSize: "13px"}}>{screenshot.username}</span>
                <span style={{position: "absolute", top: "361px", left: "30px", fontSize: "12px", color: "#a2a2a2"}}>Keep • {screenshot.keepTitle}</span>
                <span style={{position: "absolute", top: "387px", left: "32px", fontSize: "50px", color: "#57525d", fontFamily: "keeprun"}}>{screenshot.miles.toFixed(2)}<span style={{fontSize: "10.67px", marginLeft: "5px"}}>公里</span></span>
                {/* 添加其他显示元素 */}
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>数据设置</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>用户名</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    value={screenshot.username}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Keep标题</Form.Label>
                  <Form.Control
                    type="text"
                    name="keepTitle"
                    value={screenshot.keepTitle}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>公里数</Form.Label>
                  <Form.Control
                    type="number"
                    name="miles"
                    value={screenshot.miles}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>背景图片</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handleBgImageChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>头像</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handlePortraitChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>天气</Form.Label>
                  <Form.Select onChange={handleWeatherChange}>
                    <option value="/images/weather1.png">晴</option>
                    <option value="/images/weather2.png">多云</option>
                    <option value="/images/weather3.png">阴天</option>
                  </Form.Select>
                </Form.Group>
                {/* 添加其他表单字段 */}
                <Button variant="primary" onClick={generateScreenshot}>
                  生成截图
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
