import React from 'react';
import { Card, Form } from 'react-bootstrap';

function SettingsCard({ onInputChange, onBgImageChange, onInitializeDrawing }) {
  return (
    <Card className="settings-card">
      <Card.Body>
        <Card.Title className="text-center mb-3">数据设置</Card.Title>
        <Form>
          {/* 添加表单字段 */}
          <Form.Group className="mb-3">
            <Form.Label>背景图片</Form.Label>
            <Form.Control
              type="file"
              onChange={(e) => onBgImageChange(e.target.files[0])}
            />
          </Form.Group>
          <button onClick={onInitializeDrawing} className="btn btn-secondary">
            绘制轨迹
          </button>
        </Form>
      </Card.Body>
    </Card>
  );
}

export default SettingsCard;
