import React, { useState, useRef } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";

function App() {
  const [screenshot, setScreenshot] = useState({
    username: "用户名",
    keepTitle: "户外跑步",
    miles: 2.5,
    speed: 5.5,
    date: new Date(),
    temperature: 12,
    humidity: 23,
  });

  const canvasRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScreenshot((prev) => ({ ...prev, [name]: value }));
  };

  const generateScreenshot = () => {
    console.log("生成截图", screenshot);
    // 这里实现生成截图的逻辑
  };

  return (
    <Container className="my-4">
      <h1 className="text-center mb-4">Keep运动截图生成器</h1>
      <Row>
        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>预览</Card.Title>
              <div
                style={{
                  width: "360px",
                  height: "719px",
                  border: "1px solid #ddd",
                }}
              >
                {/* 预览内容 */}
                <p>用户名: {screenshot.username}</p>
                <p>Keep标题: {screenshot.keepTitle}</p>
                <p>公里数: {screenshot.miles}</p>
                {/* 其他预览内容 */}
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6}>
          <Card>
            <Card.Body>
              <Card.Title>数据设置</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>用户名</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    value={screenshot.username}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Keep标题</Form.Label>
                  <Form.Control
                    type="text"
                    name="keepTitle"
                    value={screenshot.keepTitle}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>公里数</Form.Label>
                  <Form.Control
                    type="number"
                    name="miles"
                    value={screenshot.miles}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                {/* 添加其他表单字段 */}
                <Button variant="primary" onClick={generateScreenshot}>
                  生成截图
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
