import React, { useState, useRef, useEffect } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";
import html2canvas from 'html2canvas';
import './App.css'; // 确保创建并导入这个CSS文件
// 使用 require 语句导入图片
const defaultBg = require('./images/default_bg.png');
const defaultPortrait = require('./images/default_portrait.png');

function App() {
  const [screenshot, setScreenshot] = useState({
    username: "用户名",
    keepTitle: "户外跑步",
    miles: 3.72,
    pace: "07'28\"",
    duration: "00:27:47",
    calories: 266,
    date: new Date(2024, 9, 13, 21, 29), // 2024/10/13 21:29
    temperature: 12,
    humidity: 23,
  });

  const [bgImage, setBgImage] = useState("/images/default_bg.png");
  const [portraitImage, setPortraitImage] = useState("/images/default_portrait.png");

  const newImgRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScreenshot((prev) => ({ ...prev, [name]: value }));
  };

  const handleBgImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setBgImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handlePortraitChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setPortraitImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const generateScreenshot = () => {
    html2canvas(newImgRef.current).then(canvas => {
      const link = document.createElement('a');
      link.download = `keep${screenshot.date.getMonth() + 1}月${screenshot.date.getDate()}日跑步打卡.png`;
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  return (
    <Container fluid className="app-container">
      <h1 className="text-center mb-4 app-title">Keep运动截图生成器</h1>
      <Row className="justify-content-center">
        <Col md={6} lg={5} className="mb-4">
          <Card className="preview-card">
            <Card.Body>
              <Card.Title className="text-center mb-3">预览</Card.Title>
              <div
                ref={newImgRef}
                className="preview-image"
                style={{
                  backgroundImage: `url(${bgImage})`,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center',
                }}
              >
                <div className="keep-content">
                  <div className="keep-header">
                    <span>Keep • {screenshot.keepTitle}</span>
                    <img src={portraitImage} alt="头像" className="user-avatar" />
                  </div>
                  <div className="keep-body">
                    <div className="keep-miles">
                      <span className="miles-number">{screenshot.miles.toFixed(2)}</span>
                      <span className="miles-unit">公里</span>
                    </div>
                    <div className="keep-stats">
                      <div className="stat">
                        <span className="stat-value">{screenshot.pace}</span>
                        <span className="stat-label">平均配速</span>
                      </div>
                      <div className="stat">
                        <span className="stat-value">{screenshot.duration}</span>
                        <span className="stat-label">用时</span>
                      </div>
                      <div className="stat">
                        <span className="stat-value">{screenshot.calories}</span>
                        <span className="stat-label">千卡</span>
                      </div>
                    </div>
                  </div>
                  <div className="keep-footer">
                    <span>{screenshot.date.toLocaleString('zh-CN', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', hour12: false })}</span>
                    <span>{screenshot.temperature}°C {screenshot.humidity}%</span>
                  </div>
                </div>
                <div className="keep-button">
                  <button>打卡到 Keep 动态</button>
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6} lg={5}>
          <Card className="settings-card">
            <Card.Body>
              <Card.Title className="text-center mb-3">数据设置</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>用户名</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    value={screenshot.username}
                    onChange={handleInputChange}
                    className="custom-input"
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Keep标题</Form.Label>
                  <Form.Control
                    type="text"
                    name="keepTitle"
                    value={screenshot.keepTitle}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>公里数</Form.Label>
                  <Form.Control
                    type="number"
                    step="0.01"
                    name="miles"
                    value={screenshot.miles}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>平均配速</Form.Label>
                  <Form.Control
                    type="text"
                    name="pace"
                    value={screenshot.pace}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>用时</Form.Label>
                  <Form.Control
                    type="text"
                    name="duration"
                    value={screenshot.duration}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>消耗卡路里</Form.Label>
                  <Form.Control
                    type="number"
                    name="calories"
                    value={screenshot.calories}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>日期时间</Form.Label>
                  <Form.Control
                    type="datetime-local"
                    name="date"
                    value={screenshot.date.toISOString().slice(0, 16)}
                    onChange={(e) => handleInputChange({
                      target: {
                        name: 'date',
                        value: new Date(e.target.value)
                      }
                    })}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>温度 (°C)</Form.Label>
                  <Form.Control
                    type="number"
                    name="temperature"
                    value={screenshot.temperature}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>湿度 (%)</Form.Label>
                  <Form.Control
                    type="number"
                    name="humidity"
                    value={screenshot.humidity}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>背景图片</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handleBgImageChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>头像</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handlePortraitChange}
                  />
                </Form.Group>
                <Button variant="primary" onClick={generateScreenshot} className="generate-btn">
                  生成截图
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
