import React, { useState, useRef } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";
import html2canvas from "html2canvas";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import "./App.css"; // 确保创建并导入这个CSS文件

function App() {
  const [screenshot, setScreenshot] = useState({
    username: "御膳爱吃的_keeper1660",
    keepTitle: "Keep·哈尔滨工程大学南操场跑步",
    miles: 3.64,
    pace: "03'35\"",
    duration: "00:13:03",
    calories: 256,
    date: new Date(2023, 9, 21, 16, 27), // 2023/10/21 16:27
    temperature: 19,
    humidity: 44,
    ranking: "NO.9",
    ageGroup: "18-22 岁R4 等级 男子组",
  });

  const [bgImage, setBgImage] = useState("/images/bg1_1.png");
  const [portraitImage, setPortraitImage] = useState(
    "/images/default_portrait.png"
  );

  const newImgRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setScreenshot((prev) => ({ ...prev, [name]: value }));
  };

  const handleBgImageChange = (e) => {
    const selectedBg = e.target.value;
    setBgImage(selectedBg);
  };

  const handlePortraitChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setPortraitImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleCustomBgImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => setBgImage(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const generateScreenshot = () => {
    html2canvas(newImgRef.current).then((canvas) => {
      const link = document.createElement("a");
      link.download = `keep${
        screenshot.date.getMonth() + 1
      }月${screenshot.date.getDate()}日跑步打卡.png`;
      link.href = canvas.toDataURL();
      link.click();
    });
  };

  return (
    <Container fluid className="app-container">
      <h1 className="text-center mb-4 app-title">Keep运动截图生成器</h1>
      <Row className="justify-content-center">
        <Col md={6} lg={5} className="mb-4">
          <Card className="preview-card">
            <Card.Body>
              <Card.Title className="text-center mb-3">预览</Card.Title>
              <div
                ref={newImgRef}
                className="preview-image"
                style={{
                  backgroundImage: `url(${process.env.PUBLIC_URL + bgImage})`,
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                }}
              >
                <div className="keep-header-icons">
                  <div className="back-icon">
                    <FontAwesomeIcon icon="chevron-left" />
                  </div>
                  <div className="more-icon">
                    <FontAwesomeIcon icon="ellipsis-v" />
                  </div>
                </div>
                <div className="keep-map">
                  <div className="track-button">轨迹视频</div>
                </div>
                <div className="keep-content">
                  <div className="keep-title">{screenshot.keepTitle}</div>
                  <div className="keep-user">
                    <img
                      src={process.env.PUBLIC_URL + portraitImage}
                      alt="头像"
                      className="user-avatar"
                    />
                    <span className="username">{screenshot.username}</span>
                  </div>
                  <div className="keep-miles">
                    <span className="miles-number">
                      {screenshot.miles.toFixed(2)}
                    </span>
                    <span className="miles-unit">公里</span>
                  </div>
                  <div className="keep-progress-bar"></div>
                  <div className="keep-stats">
                    <div className="stat">
                      <span className="stat-value">{screenshot.pace}</span>
                      <span className="stat-label">平均配速</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">
                        {screenshot.duration}
                      </span>
                      <span className="stat-label">用时</span>
                    </div>
                    <div className="stat">
                      <span className="stat-value">
                        {screenshot.calories}
                      </span>
                      <span className="stat-label">千卡</span>
                    </div>
                  </div>
                  <div className="keep-footer">
                    <span>
                      {screenshot.date.toLocaleString("zh-CN", {
                        year: "numeric",
                        month: "2-digit",
                        day: "2-digit",
                        hour: "2-digit",
                        minute: "2-digit",
                        hour12: false,
                      })}
                    </span>
                    <span>
                      <span className="weather-icon">☀️</span> {screenshot.temperature}°C {screenshot.humidity}%
                    </span>
                  </div>
                </div>
                <div className="keep-ranking">
                  <div className="ranking-icon">🏅</div>
                  <div className="ranking-text">
                    <div>3 公里成绩 {screenshot.ranking}</div>
                    <div className="ranking-subtext">{screenshot.ageGroup}</div>
                  </div>
                  <div className="ranking-arrow">&#8250;</div>
                </div>
                <div className="keep-button">
                  <button>跑步记录分享</button>
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6} lg={5}>
          <Card className="settings-card">
            <Card.Body>
              <Card.Title className="text-center mb-3">数据设置</Card.Title>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>用户名</Form.Label>
                  <Form.Control
                    type="text"
                    name="username"
                    value={screenshot.username}
                    onChange={handleInputChange}
                    className="custom-input"
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Keep标题</Form.Label>
                  <Form.Control
                    type="text"
                    name="keepTitle"
                    value={screenshot.keepTitle}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>公里数</Form.Label>
                  <Form.Control
                    type="number"
                    step="0.01"
                    name="miles"
                    value={screenshot.miles}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>平均配速</Form.Label>
                  <Form.Control
                    type="text"
                    name="pace"
                    value={screenshot.pace}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>用时</Form.Label>
                  <Form.Control
                    type="text"
                    name="duration"
                    value={screenshot.duration}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>消耗卡路里</Form.Label>
                  <Form.Control
                    type="number"
                    name="calories"
                    value={screenshot.calories}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>日期时间</Form.Label>
                  <Form.Control
                    type="datetime-local"
                    name="date"
                    value={screenshot.date.toISOString().slice(0, 16)}
                    onChange={(e) =>
                      handleInputChange({
                        target: {
                          name: "date",
                          value: new Date(e.target.value),
                        },
                      })
                    }
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>温度 (°C)</Form.Label>
                  <Form.Control
                    type="number"
                    name="temperature"
                    value={screenshot.temperature}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>湿度 (%)</Form.Label>
                  <Form.Control
                    type="number"
                    name="humidity"
                    value={screenshot.humidity}
                    onChange={handleInputChange}
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>背景图片</Form.Label>
                  <Form.Select onChange={handleBgImageChange} value={bgImage}>
                    <option value="/images/bg1_1.png">背景 1</option>
                    <option value="/images/bg1_2.png">背景 2</option>
                    <option value="/images/bg1_3.png">背景 3</option>
                    <option value="/images/bg1_4.png">背景 4</option>
                    <option value="/images/bg1_5.png">背景 5</option>
                    <option value="/images/bg1_empty.png">空白背景 1</option>
                    <option value="/images/bg1_empty2.png">空白背景 2</option>
                    <option value="/images/bg2_empty.png">空白背景 3</option>
                    <option value="/images/bg3_empty.png">空白背景 4</option>
                  </Form.Select>
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>自定义背景图片</Form.Label>
                  <Form.Control
                    type="file"
                    accept="image/*"
                    onChange={handleCustomBgImageUpload}
                  />
                </Form.Group>
                <Button
                  variant="primary"
                  onClick={generateScreenshot}
                  className="generate-btn"
                >
                  生成截图
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
